//! Variables
const header = $(".header"),
  headerMenu = $(".header__menu"),
  headerMenuInsideElm = $(".header__menu *"),
  headerHamburgerMenu = $(".header__justify"),
  headerHamburgerMenuUnderline = $(".header__list-underline-justify"),
  mainDarkBackground = $(".main__bg"),
  headerSearch = $(".header__search input"),
  headerSearchOpener = $(".header__search-opener"),
  headerSearchClose = $(".header__search-close"),
  headerSearchTrash = $(".header__search-trash"),
  headerSearchRecently = $(".header__search-last"),
  slider = $(".slider"),
  sliderButtons = $(".slider__btnGroup"),
  bottomMenu = $(".bottomMenu"),
  frequentlyDarkBackground = $(".frequently__bg"),
  frequentlyMoreOpener = $(".frequently__more"),
  frequentlyMoreCloser = $(".frequently__service-close"),
  headerTopRow = $("#HeaderTopRow"),
  footerOpener = $(".footer__open"),
  footerCloser = $(".footer__close"),
  footerAbout = $(".footer__about"),
  footerAboutgradient = $(".footer__about-gradient"),
  phoneCategory = $(".phoneCategory"),
  bottomMenuCategory = $(".bottomMenu__category"),
  bottomMenuHome = $(".bottomMenu__home"),
  mainSection = $("main section"),
  headerSectionBanner = $("#HeaderSec1 img"),
  headerSectionSearch = $("#HeaderSec2"),
  headerMenuOnHoverHandler = () => {
    $("html").css("overflow", "hidden"),
      headerMenuInsideElm.css("opacity", "1"),
      headerMenu.css("visibility", "visible"),
      headerMenu.animate({ opacity: "1" }),
      headerHamburgerMenuUnderline.css("width", "112%"),
      navigator.userAgent.search("Chrome") >= 0 &&
        (header.css("padding-right", "5px"), mainDarkBackground.css("display", "block"));
  },
  headerMenuOffHoverHandler = () => {
    $("html").css("overflow", "auto"),
      headerMenuInsideElm.css("opacity", "0"),
      headerMenu.css({ visibility: "hidden", opacity: "0" }),
      headerHamburgerMenuUnderline.css("width", "0%"),
      navigator.userAgent.search("Chrome") >= 0 &&
        (header.css("padding-right", "0px"), mainDarkBackground.css("display", "none"));
  },
  headerSearchClickHandler = () => {
    $("html").css("overflow", "hidden"),
      headerSearchRecently.css("display", "block"),
      headerSearchOpener.css("visibility", "visible"),
      headerSearchOpener.animate({ height: "100vh" });
  },
  headerSearchCloseClickHandler = () => {
    $("html").css("overflow", "auto"), headerSearchOpener.css({ visibility: "hidden", height: "0" });
  },
  headerSearchTrashClickHandler = () => {
    headerSearchRecently.css("display", "none");
  },
  sliderButtonsOnHoverHandler = () => {
    sliderButtons.animate({ opacity: "1" }, 400);
  },
  sliderButtonsOffHoverHandler = () => {
    sliderButtons.animate({ opacity: "0" }, 200);
  },
  frequentlyMoreOpenerclickHandler = () => {
    $("html").css("overflow", "hidden"),
      frequentlyDarkBackground.css("visibility", "visible"),
      frequentlyDarkBackground.animate({ opacity: "1" }),
      bottomMenu.css("visibility", "hidden"),
      headerTopRow.css("visibility", "hidden"),
      $(".homeBtn").css("display", "none");
  },
  frequentlyMoreCloserclickHandler = () => {
    $("html").css("overflow", "auto"),
      frequentlyDarkBackground.css({ visibility: "hidden", opacity: "0" }),
      bottomMenu.css("visibility", "visible"),
      headerTopRow.css("visibility", "visible"),
      $(".homeBtn").css("display", "block");
  },
  footerOpenerTextClickHandler = () => {
    footerAbout.css({ overflow: "visible", display: "block" }),
      footerOpener.css("visibility", "hidden"),
      footerAboutgradient.css("display", "none");
  },
  footerCloserTextClickHandler = () => {
    footerAbout.css({ overflow: "hidden", display: "-webkit-box" }),
      footerOpener.css("visibility", "visible"),
      footerAboutgradient.css("display", "inline");
  },
  categoryButtonClickHandler = () => {
    mainSection.css({ display: "none", opacity: "0" }),
      phoneCategory.css("display", "block"),
      phoneCategory.animate({ opacity: "1" }, 400),
      bottomMenuHome.css("color", "#a1a3a8"),
      headerSectionBanner.css({ "min-height": "40px", "object-fit": "cover" }),
      $(".header__banner").css({ "min-height": "40px" }),
      headerSectionSearch.css("display", "none"),
      bottomMenuCategory.css("color", "#303030"),
      $(".homeBtn").css("display", "none");
  },
  homeButtonClickHandler = () => {
    mainSection.css("display", "block"),
      mainSection.animate({ opacity: "1" }, 300),
      phoneCategory.css({ display: "none", opacity: "0" }),
      bottomMenuCategory.css("color", "#a1a3a8"),
      headerSectionBanner.css({ "min-height": "auto", "object-fit": "cover" }),
      $(".header__banner").css({ "min-height": "auto" }),
      headerSectionSearch.css("display", "block"),
      $(".homeBtn").css("display", "block"),
      bottomMenuHome.css("color", "#303030");
  },
  goUpButtonClickHandler = () => {
    $("html, body").animate({ scrollTop: 0 }, 650);
  }; //! Calling Functions
//! Header Menu
headerHamburgerMenu.hover(headerMenuOnHoverHandler, headerMenuOffHoverHandler),
  headerMenu.hover(headerMenuOnHoverHandler, headerMenuOffHoverHandler), //! Header SearchBar
  headerSearch.on("click", headerSearchClickHandler), //! Slider Buttons
  slider.hover(sliderButtonsOnHoverHandler, sliderButtonsOffHoverHandler), //! Frequently Outside Click Handler
  $(frequentlyDarkBackground).on("click", (e) => {
    let r = e.target;
    r.closest(".frequently__service") || frequentlyMoreCloserclickHandler();
  }); //! All Swiper.Js Codes
//! Initialize Swiper Story
var swiper = new Swiper(".mySwiper5", {
  slidesPerView: "auto",
  spaceBetween: 15,
  navigation: { nextEl: ".swiper-button-next", prevEl: ".swiper-button-prev" },
}); //! Initialize Swiper Slider
const progressCircle = document.querySelector(".autoplay-progress svg"),
  progressContent = document.querySelector(".autoplay-progress span");
var swiper = new Swiper(".mySwiper", {
    spaceBetween: 0,
    loop: !0,
    centeredSlides: !0,
    autoplay: { delay: 6500, disableOnInteraction: !1 },
    pagination: { el: ".swiper-pagination", clickable: !0 },
    navigation: { nextEl: ".swiper-button-next", prevEl: ".swiper-button-prev" },
    on: {
      autoplayTimeLeft(e, r, o) {
        progressCircle.style.setProperty("--progress", 1 - o),
          (progressContent.textContent = `${Math.ceil(r / 1e3)}`);
      },
    },
  }),
  swiper = new Swiper(".mySwiper2", {
    slidesPerView: "auto",
    spaceBetween: 3.5,
    navigation: { nextEl: ".swiper-button-next", prevEl: ".swiper-button-prev" },
  }),
  swiper = new Swiper(".mySwiper3", {
    slidesPerView: "auto",
    spaceBetween: 10,
    navigation: { nextEl: ".swiper-button-next", prevEl: ".swiper-button-prev" },
  }),
  swiper = new Swiper(".mySwiper4", {
    slidesPerView: "auto",
    spaceBetween: 20,
    navigation: { nextEl: ".swiper-button-next", prevEl: ".swiper-button-prev" },
  });
